INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (16, 'Flaky Buttermilk Biscuits', 'Cooks Illustrated (All-Time Best Recipes 2010)', 0, 0, 0, 12, 'For soured milk, add 1 tbsp lemon juice or white vinegar to 1 cup milk', 0, 0, 0, 0, 0, 0, 0, 'Adjust oven rack to lower-middle position; heat oven to 450 degrees.  
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (16, '8 tablespoon unsalted butter, cold, lightly floured and cut into 1/8-inch slices
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (16, '2 tablespoon vegetable shortening, cut into 1/2-inch chunks
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (16, '1 teaspoon salt
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (16, '1 tablespoon baking powder
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (16, '1/2 teaspoon baking soda
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (16, '2 1/2 cup unbleached all-purpose flour
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (16, '2 tablespoon butter, melted
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (16, '1 1/4 cup buttermilk, cold');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (16, 'biscuits');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (16, 'bread');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (16, 'sides');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, 'Adjust oven rack to lower-middle position; heat oven to 450 degrees.  ');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, 'Whisk flour, baking power, baking soda, and salt in large bowl.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, 'Add shortening to flour mixture; break up chunks with fingertips until only pea-sized pieces remain.  Working in batches, drop butter slices into flour mixture and toss to coat; pick up each slice of butter and press between floured fingertips into flat, nickel-sized pieces.  Repeat until all butter is incorporated; toss to combine.  Freeze mixture (in bowl) until chilled, about 15 minutes.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, 'Spray 24-square-inch work surface with nonstick cooking spray; spread spray evenly across surface with kitchen towel or paper towel.  Sprinkle 1/3 cup of extra flour across sprayed area; gently spread flour across work surface with palm to form thin, even coating.  Add all but 2 tablespoons of buttermilk to flour mixture; stir briskly with fork until ball forms and no dry bits of flour are visible, adding remaining buttermilk as needed (dough will be sticky and shaggy but should clear sides of bowl).  With rubber spatula, transfer dough onto center of prepared work surface, dust surface lightly with flour, and, with floured hands, bring dough together into cohesive ball.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, 'Pat dough into approximate 10-inch square; roll into 18- by 14-inch rectangle about 1/4 inch thick, dusting dough and rolling pin with flour as needed.  Using bench scraper or thin metal spatula, fold dough into thirds, brushing any excess flour from surface; lift short end of dough and fold in thirds again to form approximate 6- by 4-inch rectangle.  Rotate dough 90 degrees, dusting work surface underneath with flour; roll and fold dough again, dusting with flour as needed.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, 'Roll dough into 10-inch square about 1/2 inch thick; flip dough and cut nine 3-inch rounds with floured biscuit cutter, dipping cutter back into flour after each cut.  Carefully invert and transfer rounds to ungreased baking sheet, spaced 1 inch apart.  Gather dough scraps into ball; roll and fold once or twice until scraps form smooth dough.  Roll dough into 1/2-inch-thick round; cut 3 more 3-inch rounds and transfer to baking sheet.  Discard excess dough.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (16, 'Brush biscuit tops with melted butter.  Bake, without opening oven door, until tops are golden brown and crisp, 15 to 17 minutes.  Let cool on baking sheet 5 to 10 minutes before serving.');
