INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1043, 'Parker''s split pea soup', 'http://www.foodnetwork.com/recipes/ina-garten/parkers-split-pea-soup-recipe/index.html', 600, 0, 5400, 6, '', 0, 0, 0, 0, 0, 0, 0, 'In a 4-quart stockpot on medium heat, saute the onions and garlic with the olive oil, oregano, salt, and pepper until the onions are translucent, 10 to 15 minutes. Add the carrots, potatoes, 1/2 pound of split peas, and chicken stock. Bring to a boil, then simmer uncovered for 40 minutes. Skim off the foam while cooking. Add the remaining split peas and continue to simmer for another 40 minutes, or until all the peas are soft. Stir frequently to keep the solids from burning on the bottom. Taste for salt and pepper. Serve hot.');
