INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1113, 'Melomakarona - Honey Cookies with Walnuts', 'http://greekfood.about.com/od/cookiescakes/r/Melomakarona-Spiced-Honey-Cookies-With-Walnuts.htm', 0, 0, 0, 1, 'This is an oil-based cookie recipe that produces moist cake-like cookies flavored with orange and brandy that are bathed in a sweet honey syrup and topped with chopped walnuts.', 0, 0, 0, 0, 0, 0, 0, 'Preheat the oven to 350 degrees.
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '1 cup olive oil
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '1 cup vegetable oil
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '3/4 cup sugar
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '1 orange, zested
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '3/4 cup orange juice
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '1/4 cup brandy
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '2 tsp baking powder
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '1 tsp baking soda
