INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, 'Ground cinnamon for sprinkling
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '<hr>
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '1 cup honey
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '1 cup sugar
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '1 1/2 cup water
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '1 cinnamon stick
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1113, '4 whole cloves
