INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1114, '2 tablespoon all-purpose flour
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1114, '1 egg, beaten (for glaze)
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1114, '<hr>
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1114, 'ground cinnamon (for baked pastries)
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1114, 'confectioner''s sugar or honey (for fried pastries)');
