INSERT INTO preparationSteps (recipe_id, step) VALUES (1118, 'Comine all ingredients and blend well.  This may require a mixer since the sugar mixture will be thick.  Fold in pecans and pour into medium unbaked pastry shell.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1118, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1118, 'Bake on cookie sheet at 350 degrees for 50 minutes.');
INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1119, 'Asian Salad', 'Pampered Chef', 0, 0, 0, 2, '', 140, 0, 0, 32, 8, 0, 2, 'In a small bowl, whisk together sugar, vinegar, salt and black pepper; set aside.
