INSERT INTO preparationSteps (recipe_id, step) VALUES (1210, 'To char the chiles (or any fresh chile): Put the chiles over a gas flame or underneath the broiler and cook until they are blackened on all sides. Enclose them in a plastic bag and let stand for 10 minutes to steam, which will make them easier to peel. Peel, stem, and seed the chiles. Once peeled, the chiles may be chopped, sliced or stuffed.');
INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1212, 'Hawaiian style short ribs (slow cooker)', 'Everyday Food Nov 2011', 1200, 0, 18000, 6, '', 0, 0, 0, 0, 0, 0, 0, 'Place onions, garlic, and ginger in a 5- to 6-quart slow cooker. Top with short ribs in a tight layer. In a medium bowl, whisk together brown sugar, soy sauce, vinegar, and Sriracha and add to slow cooker. Cover and cook on high until ribs are almost tender, 4 hours. Add pineapple and cook until pineapple is tender, 1 hour.
