INSERT INTO preparationSteps (recipe_id, step) VALUES (1232, 'Pour the batter into the prepared pan. Bake until a toothpick comes out with a few crumbs, about 35 minutes. Cool in the pan on a rack 1 hour, then run a knife around the edge of the pan and remove the ring. Let cool completely.');
