INSERT INTO preparationSteps (recipe_id, step) VALUES (1233, 'Meanwhile, put the white chocolate in a heatproof bowl and set over a saucepan of barely simmering water (do not let the bowl touch the water); stir until the chocolate melts. Drizzle the cookies with the melted white chocolate and sprinkle with the pistachios. Let set at room temperature, about 15 minutes.');
INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1234, 'Chocolate babka', 'Food Network Magazine March 2012', 3600, 19800, 3600, 8, '', 0, 0, 0, 0, 0, 0, 0, 'Make the dough: Combine the yeast and 2 tablespoons warm water in the bowl of a stand mixer; set aside until foamy, 5 minutes. Add the milk, sour cream, egg yolk and 1/2 cup flour; beat with the paddle attachment on medium speed until combined. Gradually beat in the remaining 1 1/4 cups flour, scraping the bowl as needed, to form a wet dough, about4 minutes. Increase the speed to medium high; add the whole egg, sugar, salt and vanilla and beat until creamy, 3 to 4 minutes. Add the butter, 1 tablespoon at a time; beat until incorporated. The dough will be very wet.
