INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1287, 'Mixed Tomato Cobbler with Gruyere Crust', 'http://www.marthastewart.com/316237/mixed-tomato-cobbler-with-gruyere-crust?czone=food%2Fcomfort-foods-center%2Fcomfort-foods-dishes&gallery=275688&slide=316237&amp;center=854190', 0, 0, 0, 8, 'Serve with a salad for a meal', 0, 0, 0, 0, 0, 0, 0, 'In the bowl of a food processor, combine 2 1/2 cups flour, 1 teaspoon salt, 1 teaspoon granulated sugar, and 1 cup Gruyere cheese. Add 1 cup butter; process until mixture resembles coarse meal, 8 to 10 seconds.
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1287, '2 3/4 cup all-purpose flour
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1287, '2 tablespoon all-purpose flour
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1287, '2 1/2 teaspoon salt
