INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1287, '1 cup unsalted butter, cold, cut into pieces
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1287, '1 tablespoon unsalted butter
