INSERT INTO preparationSteps (recipe_id, step) VALUES (1291, 'In the bottom of a shallow 2-quart casserole dish, spread half the tomato sauce. Top with ziti, then ricotta mixture and remaining sauce. Sprinkle with remaining Parmesan and mozzarella. Place casserole on a rimmed baking sheet, and bake until top is browned and sauce is bubbling, 20 to 25 minutes.');
