INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1292, 'Millet bread (Corn-free cornbread)', 'http://gingerlemongirl.blogspot.com/2010/11/gluten-free-vegan-millet-bread-mock.html', 600, 0, 1200, 8, '', 0, 0, 0, 0, 0, 0, 0, 'Heat oven to 425 degrees. In a mixing bowl whisk together all dry ingredients. In a small bowl mix together all wet ingredients. Pour the wet ingredients into the dry ingredients and mix together thoroughly with a fork into a wet batter. Place a heavy-bottomed oven-safe skillet or cast iron pan with 2 tablespoons butter in it into the preheated oven for 5 minutes. Allow the butter to get hot enough to sizzle when a drop of water is flicked into the pan. Holding the hot pan with an oven mitt pour the millet bread batter into the pan and spread it evenly around the pan. Place back in the oven and bake for 18-20 minutes until golden brown and a toothpick inserted in the middle comes out clean. Let cool for 10 minutes before serving.
