INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1311, 'Shrimp fried rice', 'http://www.simplyrecipes.com/recipes/shrimp_fried_rice/', 900, 600, 300, 4, 'Use day old, leftover rice. Fresh rice will make it mushy.', 0, 0, 0, 0, 0, 0, 0, '1 In a bowl, toss the shrimp with the salt, pepper and cornstarch. Let marinate for 10 minutes at room temperature. Heat a wok or large sauté pan on high heat. When the pan is hot enough for a bead of water to instantly sizzle and evaporate, add just 1 tablespoon of the cooking oil and swirl to coat pan.
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1311, '8 ounce small raw shrimp, shelled and deveined
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1311, '1/2 teaspoon kosher salt
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1311, 'freshly ground black pepper
