INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1311, '4 cup leftover rice, grains separated well
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1311, '3/4 cup frozen peas and carrots, defrosted
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1311, '1 tablespoon soy sauce, (use gluten-free soy sauce if you are making a gluten-free version)
