INSERT INTO preparationSteps (recipe_id, step) VALUES (1313, '2. Heat oil in a large nonstick skillet over medium heat. Add onion. Cook, stirring often, until onion is translucent and pale golden, 10 to 12 minutes. Stir in beans, stock, and salt. Using a heatproof rubber spatula, fold in chiles; cook until chiles are warmed though, 3 to 5 minutes.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1313, '3. Remove from heat; stir in fromage blanc. Spoon into the warm tortillas. Serve with lime, and garnish with cilantro.');
INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1314, 'Muttar paneer', 'http://www.cookingchanneltv.com/recipes/nigella-lawson/muttar-paneer.html', 600, 0, 1800, 4, '', 0, 0, 0, 0, 0, 0, 0, 'Heat the oil a large skillet and add the paneer cubes, in 2 batches, and fry until they are golden. Remove the golden cubes to a double thickness of kitchen towel. (It is possible to dry fry the paneer cubes in the pan with no oil, to avoid the oil splashing you. Then continue with the recipe below.) 
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1314, '1/2 cup vegetable oil
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1314, '8 ounce paneer, cut into 1/2-inch cubes
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1314, '1 onion, halved
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1314, '2 clove garlic, roughly chopped
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1314, '1 tbsp ginger, roughly chopped
