INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1319, 'Squash and bean burritos', 'based on Chicken and bean burritos in Food Network Magazine Jan/Feb 2010', 1200, 0, 1200, 4, '', 1988, 60, 12, 280, 44, 0, 96, 'Heat a medium skillet over high heat. Add the rice and smashed garlic and cook, stirring, until fragrant, 2 to 3 minutes. Add 1 cup water, season with salt and pepper and bring to a boil. Add the squash and reduce the heat to low; cover and simmer, undisturbed, about 25 minutes; remove from the heat.
