INSERT INTO preparationSteps (recipe_id, step) VALUES (1344, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1344, 'Slice the tops off the peppers and remove all the ribs and seeds. If necessary, cut a very thin slice from the base to help the peppers stand up. Stuff the peppers with the filling and drizzle the tops with olive oil. Put the peppers in an 8 by 8-inch square baking dish. Fill the baking dish with 3/4-inch hot water and bake until the filling is golden and the peppers are cooked through, about 55 to 60 minutes.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1344, '');
