INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1349, '24 serving [fudge brownies](http://www.xanthir.com/recipes/showrecipe.php?id=id350)');
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1349, '4 ounce cream cheese, at room temperature
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1349, '2 tablespoon sugar
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1349, '1 tablespoon King Arthur Unbleached All-Purpose Flour
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1349, '1 large egg yolk
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1349, '1/2 teaspoon mint flavoring (optional)');
