INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1361, 'Greek layered dip with pita chips', 'http://www.foodnetwork.com/recipes/sandra-lee/greek-layered-dip-with-pita-chips-recipe.html', 600, 0, 0, 10, '', 0, 0, 0, 0, 0, 0, 0, 'Preheat the oven to 375 degrees F. Line 1 or 2 baking sheets with foil. Cut the pita rounds into eighths. Split each piece in two and place on the prepared baking sheet(s), with the inside of the bread facing up. Spray with the olive oil cooking spray and sprinkle with 2 teaspoons of the Greek seasoning. Bake 7 to 10 minutes, or until golden.
