INSERT INTO preparationSteps (recipe_id, step) VALUES (1375, 'To make the biscuits, combine the flour, baking powder and salt in a food processor and pulse once or twice to blend. Add the butter and pulse until it is finely chopped and the mixture has the texture of coarse cornmeal. Transfer the mixture to a bowl and add the milk, green onion, chives, and parsley. Gently stir until the dough is evenly mixed; avoid overmixing.');
