INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Form a ball with the dough and let stand for 30 minutes.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Roll dough almost paper thin, on a well-floured surface.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Using the rim of a margarita glass (about 3-4 inches across), make circle imprints into rolled dough.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Using a paring knife, make sure circles are cut all the way through.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Roll each circle of dough around a metal cannoli tube, overlaping the ends and press to seal, flaring out the edges slightly.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Fry one or two at a time in hot melted shortening (about 360°F) for approximately 1 minute, turning to brown all sides.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Remove from hot grease and drain on paper towels, seam side down.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Let cool a minute or two before trying to remove metal tube.');
