INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Squeeze Maraschino cherries with paper towels to remove all liquid.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '(If you don''t squeeze them good, you will have a pink water filling!).');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Stir in cherries and chocolate chips into the ricotta mixture, being careful not to over mix.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'For a lighter filling, you may whip 1 cup of heavy whipping cream to form stiff peaks, and fold into filling mixture at this step.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'Chill filling for about 30 minutes before piping into cooled cannoli shells.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1380, 'You may garnish the cannoli by sprinkling powdered sugar on top.');
