INSERT INTO preparationSteps (recipe_id, step) VALUES (1384, '2. Add the flour and mix just until the dough comes together. Wrap the dough in plastic and chill for 30 minutes. The dough can chill longer than 30 minutes, but if it does, allow about 15 minutes for it to warm a bit and soften before rolling it out.');
