INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1418, 'Poblano, mushroom and potato tacos', 'http://www.foodnetwork.com/recipes/food-network-kitchens/poblano-mushroom-and-potato-tacos-recipe.html', 900, 0, 2100, 6, '', 0, 0, 0, 0, 0, 0, 0, 'Roast the poblanos directly over the flame of a gas burner or under the broiler, turning with tongs, until charred all over, about 10 minutes. Transfer to a bowl, cover with a plate and let steam until cool enough to handle, about 10 minutes. Scrape off the charred skin with a paring knife; remove the stems and seeds. Cut the poblanos into strips and set aside.
