INSERT INTO preparationSteps (recipe_id, step) VALUES (1418, 'Meanwhile, put the potatoes in a large nonstick skillet and cover with cold water; season with salt. Bring to a simmer over medium-high heat and cook until just tender, 5 to 6 minutes; drain. Wipe the skillet dry.');
