INSERT INTO preparationSteps (recipe_id, step) VALUES (1418, 'Heat the olive oil in the skillet over medium-high heat. Add the potatoes and cook until golden and crisp, about 4 minutes per side. Remove with a slotted spoon and drain on paper towels. While still hot, sprinkle with half of the herbs, and salt to taste. Discard the excess oil and wipe out the skillet.');
