INSERT INTO preparationSteps (recipe_id, step) VALUES (1418, 'Heat the butter in the skillet over medium-high heat. Add the onion and cook, stirring occasionally, until golden, about 6 minutes. Add the mushrooms and cook, stirring occasionally, until golden, 6 to 8 minutes. Stir in the remaining herbs and the garlic; cook 1 minute. Add the roasted poblanos, crema, lime juice and a few spoonfuls of water; stir until warmed through, 1 to 2 minutes. Season with salt and pepper. Serve the potatoes and mushroom mixture in tortillas with assorted toppings.');
