INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1420, 'Wild mushroom and cauliflower lasagna', 'http://www.foodnetwork.com/recipes/bobby-flay/wild-mushroom-and-cauliflower-lasagna-recipe.html', 3000, 2700, 8100, 8, '', 7792, 448, 224, 576, 72, 136, 368, 'For the tomato sauce: Heat the oil in a medium saucepan over medium-high heat. Add the onions and cook until soft. Add the garlic and red pepper flakes and cook for 30 seconds. Add the tomatoes, breaking them up with a wooden spoon, and the basil and cook until thickened and the tomatoes soften and break down. season with salt and pepper.
