INSERT INTO preparationSteps (recipe_id, step) VALUES (1442, 'Preheat the remaining EVOO, twice around the pan, in a nonstick skillet over medium to medium-high heat. Cook the salmon patties for 2 to 3 minutes on each side for mini patties, 4 minutes on each side for large patties.');
