INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, '1/2 lime, plus several wedges for garnish
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, 'Coarse salt and freshly ground black pepper
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, '1 scallion, thinly sliced
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, 'Nonstick cooking spray
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, '<hr>
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, '1 cup couscous
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, '1/2 lime, zested
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, '2 cup water
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, '2 teaspoon vegetable oil
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1445, '1 teaspoon coarse salt
