INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, '*To prepare zoodles (optional): In a large skillet, heat 1 teaspoon olive oil over medium-high heat. Add half of the noodles and cook, using tongs to toss occasionally, until the noodles are tender but not mushy, 3 to 5 minutes. Season with salt and pepper. Transfer the noodles to a plate. Add another teaspoon of oil to the pan and repeat the cooking process with the remaining zucchini noodles. Transfer to the plate and lightly tent with foil to keep warm while you prepare the shrimp scampi.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, 'Rinse the shrimp and pat dry; set aside.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, 'In a heavy frying pan, melt the butter and olive oil over medium heat. Lower the heat and add garlic and red pepper flakes. Cook over low heat, stirring occasionally, for 5 minutes');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, 'Raise the heat to high and when oil is hot, add the shrimp. Cook, stirring frequently, until the shrimp are pink and opaque, about 3 minutes.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, 'Add the wine and stir, scraping up any browned bits from the bottom of the pan. Cook for 1 minute to let the alcohol burn off slightly.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, 'Remove the pan from heat. Add the salt, pepper, lemon juice, lemon zest, and chopped parsley.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, 'Return pan to high heat and cook for a minute or two until butter sauce is slightly thickened.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1448, 'Serve the shrimp scampi over your choice of pasta or zucchini noodles.');
