INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1461, 'Salt-baked walnut brioche scones', 'Bitterman salt company', 900, 3600, 1080, 8, '', 2968, 200, 72, 248, 16, 104, 56, 'Place the tempered salt block on a heavy baking sheet and place on the middle rack of the oven. Turn on the oven to 350F, and heat the block for 1 hour.
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1461, '1 1/2 cup all-purpose flour
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1461, '1/2 cup sugar
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1461, '1 1/2 tsp baking powder
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1461, '1 pinch fine salt (Himalayan salt is preferred)
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1461, '6 tbsp unsalted butter, softened, cut into 12 pieces
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1461, '6 extra-large egg yolks
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1461, '1/4 cup heavy cream
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1461, '1 cup walnuts, chopped');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1461, 'dessert');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1461, 'breakfast');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1461, 'Place the tempered salt block on a heavy baking sheet and place on the middle rack of the oven. Turn on the oven to 350F, and heat the block for 1 hour.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1461, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1461, 'In the work bowl of a food processor equipped with a steel blade, combine the flour, sugar, baking powder and salt. Process in short pulses to combine. Add the butter and process in 2 to 3 pulses until it is dispersed evenly.Add the egg yolks and cream and process until the mixture becomes a cohesive dough, about 40 seconds. Add the walnuts and process in pulses until combined, but not so much that the walnuts become finely ground.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1461, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1461, 'Turn the dough out onto a clean work surface and pat into a 9-inch circle about 3/4-inch thick. Cut into 8 wedges. Arrange the cut scones on the hot salt block and bake until puffed and browned, about 18 minutes.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1461, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1461, 'Remove from the salt block with a spatula and let cool on a rack for 5 minutes before serving.');
INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1462, 'Hot buttered rum', 'http://www.foodnetwork.com/recipes/emeril-lagasse/hot-buttered-rum-cocktail-recipe.html', 300, 0, 0, 12, '', 0, 0, 0, 0, 0, 0, 0, 'In a bowl, cream together the butter, sugar, cinnamon, nutmeg, cloves, and salt. Refrigerate until almost firm. Spoon about 2 tablespoons of the butter mixture into 12 small mugs. Pour about 3 ounces of rum into each mug (filling about halfway). Top with boiling water (to fill the remaining half), stir well, and serve immediately.');
