INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1462, '2 cup light brown sugar
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1462, '1 teaspoon ground cinnamon
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1462, '1/2 teaspoon grated nutmeg
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1462, '1 Pinch ground cloves
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1462, '1 Pinch salt
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1462, '1 Bottle dark rum
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1462, 'Boiling water');
