INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1467, 'Couscous Salad with Roasted Vegetables and Chickpeas', 'http://www.marthastewart.com/315061/couscous-salad-with-roasted-vegetables-a?czone=food%2Fvegetarian-cnt%2Fvegetarian-favorite-recipes&gallery=360580&slide=315061&center=852566', 1800, 0, 1800, 4, '', 0, 0, 0, 0, 0, 0, 0, 'Preheat oven to 450 degrees. Place carrots and cauliflower on a rimmed baking sheet; toss with cumin and 2 tablespoons oil. Season with salt and pepper. Spread half the vegetables on a second baking sheet. Roast until browned and tender, 25 to 30 minutes, rotating sheets and tossing halfway through. Cool to room temperature.
