INSERT INTO preparationSteps (recipe_id, step) VALUES (1467, 'In a large bowl, combine roasted vegetables with couscous, chickpeas, and scallions. Place arugula on a serving platter, and drizzle with 1 tablespoon dressing. Add remaining dressing to couscous mixture, and toss; serve over arugula.');
