INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1471, 'Udon noodle soup', 'Blue Apron', 900, 0, 2100, 2, '', 1220, 60, 9, 138, 12, 22, 40, 'Cook & peel the eggs:
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '2 large eggs
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '2 clove garlic, minced or pressed
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '1 1-inch piece ginger, grated or finely minced
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '2 scallions, thinly sliced, white and green parts separated
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '1 carrot, diced
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '1/4 cup sweet white miso paste
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '1 tbsp hoisin sauce
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '5 cup water
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '4 oz baby bok choy, roughly chopped
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '1/2 lb fresh udon noodles
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '1 lime, juiced
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '1 tbsp sesame oil
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, '3 tbsp peanuts, roasted
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1471, 'salt and pepper');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1471, 'vegetarian');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1471, 'main');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'Cook & peel the eggs:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'Bring 1/2-inch of water to boil over high heat in a small saucepan. Carefully add the eggs to the pot of boiling water. Cover, reduce the heat to medium and cook for exactly 6 1/2 minutes. Drain thoroughly and rinse under cold water for 30 seconds to 1 minute to stop the cooking process. When cool enough to handle, carefully peel the cooked eggs. Transfer to a cutting board and set aside in a warm place.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'Cook the aromatics:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'While the eggs cook, in a large pot, heat 2 teaspoons of olive oil on medium-high until hot.Add the garlic, ginger and white bottoms of the scallions. Cook, stirring constantly, 30 seconds to 1 minute, or until fragrant. Add the carrot; season with salt and pepper. Cook, stirring occasionally, 2 to 3 minutes, or until slightly softened.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'Start the soup:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'While the eggs continue to cook, add the miso paste, hoisin sauce and 5 cups of water to the pot of aromatics. Heat to boiling on high. Once boiling, reduce the heat to medium and cook, stirring occasionally, 7 to 9 minutes, or until reduced in volume by about ¼. Add the bok choy and season with salt and pepper. Cook, stirring occasionally, 4 to 6 minutes, or until the bok choy stems have softened and the leaves have wilted.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'Finish the soup:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'To the pot, add the noodles (carefully separating with your hands before adding), sesame oil and the lime juice. Cook, stirring occasionally, 2 to 4 minutes, or until thoroughly combined and heated through. Turn off the heat; season with salt and pepper to taste.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'Plate your dish:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1471, 'Divide the finished soup between 2 bowls. Halve the peeled eggs lengthwise and season with salt and pepper. Top the soup with the seasoned eggs. Garnish with the peanuts and green tops of the scallions. Enjoy!');
INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1472, 'Oaxaca Cheese & Plantain Tortas with Tangelo & Radish Salad', 'Blue Apron', 900, 0, 1800, 2, '', 1580, 55, 20, 228, 20, 102, 56, 'Prepare the ingredients:
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '2 Sandwich Rolls
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '4 Ounces Queso Oaxaca
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '3 Radishes
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '1 Black Plantain
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '1 Romaine Heart
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '1 Tangelo
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '2 Roasted Piquillo Peppers
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '1 Shallot
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '1 Tablespoon Sugar
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1472, '1 Tablespoon Red Wine Vinegar');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1472, 'vegetarian');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1472, 'main');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1472, 'untried');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'Prepare the ingredients:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'Peel the plantain; Halve the rolls.Peel and thinly slice the shallot.Peel the plantain; cut on an angle into ¼-inch-thick pieces.Thinly slice the cheese.Cut off and discard the ends of the radishes; thinly slice into rounds.Peel and large dice the tangelo.Cut off and discard the root end of the romaine; roughly chop the leaves.Finely chop the peppers.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'Marinate the shallot:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'In a medium bowl, combine the shallot, sugar and vinegar; drizzle with olive oil and season with salt and pepper. Set aside to marinate, stirring occasionally, for at least 10 minutes. Season with salt and pepper to taste.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'Cook the plantain:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'While the shallot marinates, in a large pan (nonstick, if you have one), heat 1 teaspoon of olive oil on medium-high until hot. Add the plantain and season with salt and pepper. Cook 3 to 4 minutes on the first side, or until browned. Flip and cook 2 to 3 minutes, or until browned and softened. Transfer to a paper towel-lined plate and immediately season with salt and pepper. Wipe out the pan.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'Assemble the tortas:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'Place the rolls on a clean, dry work surface. Top the roll bottoms with the cheese, cooked plantain, peppers and marinated shallot (reserving the liquid). Season with salt and pepper. Complete the tortas with the roll tops.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'Cook the tortas:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'In the same pan, heat 2 teaspoons of olive oil on medium until hot. Add the tortas. Place a heavy-bottomed pot (or pan) on top of the tortas; press down. Cook, occasionally pressing down on the pot, 2 to 3 minutes per side, or until lightly browned and slightly flattened. Transfer to a cutting board.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'Make the salad & plate your dish:');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1472, 'In a large bowl, combine the radishes, tangelo and romaine; season with salt and pepper. Add the reserved shallot marinating liquid and a drizzle of olive oil; toss to thoroughly combine. Season with salt and pepper to taste. Cut the cooked tortas in half on an angle. Divide the tortas and salad between 2 dishes. Enjoy!');
