INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1475, '1/2 lb broccoli, cut into florets, stem diced
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1475, '6 oz oyster mushrooms, roughly chopped
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1475, '2 carrots, small diced
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1475, '2 green onions, small diced and separated
