INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1485, '24 small Brussels sprouts, trimmed and halved
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1485, '1 tbsp extra-virgin olive oil
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1485, 'Salt
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1485, 'Black pepper
