INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1492, '1 tablespoon olive tapenade
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1492, '1/4 cucumber, thinly sliced
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1492, '1 carrot, coarsely grated');
