INSERT INTO recipe_tags (recipe_id, tag) VALUES (1492, 'vegetarian');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1492, 'main');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1492, 'Spread 1 slice bread with hummus; spread remaining slice of bread with tapenade. Top hummus with cucumber slices and carrot. Place bread with tapenade on top to form a sandwich.');
