INSERT INTO preparationSteps (recipe_id, step) VALUES (1529, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1529, 'Bring one corner of the nori to the center of the filling, then bring up the opposite corner. Be firm so that the nori closes well around the filling, but don''t be so rough that the nori breaks. Repeat with remaining corners until they all meet in the center.');
