INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1548, 'Cajun Catfish and Spiced Rice', 'https://www.blueapron.com/recipes/cajun-catfish-dirty-rice-with-collard-greens-mushrooms', 900, 0, 2400, 2, '', 0, 0, 0, 0, 0, 0, 0, 'In a small pot, heat 2 tsp oil on medium. Add the **white bottoms of the scallions**, **peppers**, **half the garlic**, **spice blend**. Season with salt and pepper. Cook 30s-1m, until fragrant. Add the **tomato paste** and cook 1-2 minutes. Add the **rice**, salt, and 1 cup of water; heat to boiling on high, then cover and reduce to low, cooking for 18 minutes or until all the water has been absorbed. Fluff with a fork; season to taste.
