INSERT INTO preparationSteps (recipe_id, step) VALUES (1551, 'Using an electric mixer on medium-high speed, beat eggs, granulated sugar, and brown sugar in a large bowl until light, smooth, and doubled in volume, 3–4 minutes. Beat in salt and vanilla to combine, then beat in chocolate mixture, scraping down sides as needed, until incorporated. Beat in cornstarch mixture, then increase speed to medium-high and beat until mixture is thick and holds its shape, about 30 seconds.');
