INSERT INTO preparationSteps (recipe_id, step) VALUES (1551, 'Stir agave syrup and remaining 3 Tbsp. tahini in a small bowl. Scrape batter into prepared baking dish and smooth top. Dollop agave mixture over and swirl into top of batter with a skewer or toothpick.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1551, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1551, 'Bake brownies until sides are puffed, top is browned, and a tester inserted into the center comes out with a few moist crumbs attached, 22–26 minutes. Let cool before removing from pan and cutting into 16 squares.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1551, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1551, 'Do Ahead: Brownies can be made 3 days ahead. Wrap in plastic and store at room temperature.');
