INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1603, 'Chocolate Chip Cookies (NYT)', 'https://cooking.nytimes.com/recipes/1015819-chocolate-chip-cookies', 1200, 259200, 1080, 1, '', 0, 0, 0, 0, 0, 0, 0, 'Step 1
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '8.5 oz cake flour
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '8.5 oz bread flour
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '1 1/4 teaspoon baking soda
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '1 1/2 teaspoon baking powder
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '1 1/2 teaspoon coarse salt
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '1 1/4 cup unsalted butter (2½ sticks)
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '10 oz light brown sugar
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '8 oz granulated sugar
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '2 large eggs
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '2 teaspoon vanilla extract
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, '1 1/4 pound semisweet chocolate chips
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1603, 'Sea salt');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1603, 'cookies');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, 'Step 1');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, 'Sift flours, baking soda, baking powder and salt into a bowl. Set aside.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, 'Step 2');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, 'Using a mixer fitted with paddle attachment, cream butter and sugars together until very light, about 5 minutes. Add eggs, one at a time, mixing well after each addition. Stir in the vanilla. Reduce speed to low, add dry ingredients and mix until just combined, 5 to 10 seconds. Drop chocolate pieces in and incorporate them without breaking them. Press plastic wrap against dough and refrigerate for 24 to 36 hours. Dough may be used in batches, and can be refrigerated for up to 72 hours.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, 'Step 3');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, 'When ready to bake, preheat oven to 350 degrees. Line a baking sheet with parchment paper or a nonstick baking mat. Set aside.');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, '');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, 'Step 4');
INSERT INTO preparationSteps (recipe_id, step) VALUES (1603, 'Scoop 6 3½-ounce mounds of dough (the size of generous golf balls) onto baking sheet, making sure to turn horizontally any chocolate pieces that are poking up; it will make for a more attractive cookie. Sprinkle lightly with sea salt and bake until golden brown but still soft, 18 to 20 minutes. Transfer sheet to a wire rack for 10 minutes, then slip cookies onto another rack to cool a bit more. Repeat with remaining dough, or reserve dough, refrigerated, for baking remaining batches the next day. Eat warm, with a big napkin.');
INSERT INTO recipes (id, name, source, preptime, waittime, cooktime, servings, comments, calories, fat, satfat, carbs, fiber, sugar, protein, instructions) VALUES (1604, 'Vegetable Fried Rice with Broccoli, Carrots, & Spicy Pepper Sauce', 'Blue Apron', 1200, 0, 0, 2, '', 1180, 0, 0, 0, 0, 0, 0, 'In a medium pot, combine the **rice**, a big pinch of salt, and 1 cup of water. Heat to boiling on high. Once boiling, reduce the heat to low. Cover and cook, without stirring, 12 to 14 minutes, or until the water has been absorbed and the rice is tender. Turn off the heat and fluff with a fork.
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '1/2 cup Jasmine Rice
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '<hr>
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '1/2 lb Broccoli, roughly chopped
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '2 medium Carrots, thinly sliced at an angle
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '1 1-Inch Piece Ginger, peeled and finely chopped
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '2 Scallions, thinly sliced, separate white and green parts
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '<hr>
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '1 Tbsp White Wine Vinegar
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '1 Tbsp Honey
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '1 Bird''s Eye Chile Pepper, thinly sliced
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '<hr>
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '2 Tbsps Soy Sauce
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '3 Tbsps Sweet Chili Sauce
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '2 clove Black Garlic, peeled and roughly chopped
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1604, '2 Eggs');
INSERT INTO recipe_tags (recipe_id, tag) VALUES (1604, 'vegetarian');
