INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1614, '6 portobello mushrooms, cut into 2" pieces
INSERT INTO recipe_ingredients (recipe_id, ingredient) VALUES (1614, '2 tbsp extra-virgin olive oil
